<?php

namespace Breakdance\Admin;

use function Breakdance\BreakdanceOxygen\Strings\__bdox;

if (BREAKDANCE_MODE === 'breakdance') {
    add_action('admin_menu', 'Breakdance\Admin\breakdance_admin_menu');
} else if (BREAKDANCE_MODE === 'oxygen') {
    add_action('admin_menu', 'Breakdance\Admin\oxygen_admin_menu');
}

function breakdance_admin_menu()
{
    $formCap = \Breakdance\Forms\Submission\getFormSubmissionCapability();
    $hide_partner_discounts = \Breakdance\Data\get_global_option('settings_hide_partner_discounts') === 'yes';

    if (\Breakdance\Permissions\hasPermission('full')) {
        add_menu_page('Breakdance', 'Breakdance', 'edit_posts', 'breakdance', '', 'data:image/svg+xml;base64,');
        add_submenu_page('breakdance', 'Home', 'Home', 'edit_posts', 'breakdance', 'Breakdance\Admin\breakdanceHomePage');
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('breakdance', 'Templates', 'Templates', 'edit_posts', BREAKDANCE_TEMPLATE_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('breakdance', 'Headers', 'Headers', 'edit_posts', BREAKDANCE_HEADER_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('breakdance', 'Footers', 'Footers', 'edit_posts', BREAKDANCE_FOOTER_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('breakdance', 'Global Blocks', 'Global Blocks', 'edit_posts', BREAKDANCE_BLOCK_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        add_submenu_page('breakdance', 'Popups', 'Popups', 'edit_posts', BREAKDANCE_POPUP_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        add_submenu_page('breakdance', 'Form Submissions', 'Form Submissions', $formCap, 'edit.php?post_type=breakdance_form_res');
        add_submenu_page('breakdance', 'Design Library', 'Design Library', 'manage_options', 'breakdance_design_library', "Breakdance\DesignLibrary\getDesignLibraryAppLoader");

        if (!$hide_partner_discounts) {
            /** @psalm-suppress UndefinedFunction  */
            add_submenu_page('breakdance', 'Partner Discounts', 'Partner Discounts', 'manage_options', 'breakdance-partner-discounts', 'Breakdance\Admin\render_breakdance_partner_discounts_page');
        }
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('breakdance', 'Quickstart', 'Quickstart', 'manage_options', 'breakdance_onboarding', 'Breakdance\Onboarding\getOnboardingAppLoader');

        bdox_run_action('breakdance_admin_menu');
    } else if (\Breakdance\Forms\Submission\canViewSubmissions()) {
        add_menu_page('Breakdance Form Submissions', 'Submissions', $formCap, 'edit.php?post_type=breakdance_form_res', '', 'data:image/svg+xml;base64,');
        add_submenu_page('breakdance', 'Form Submissions', 'Form Submissions', 'editor', 'edit.php?post_type=breakdance_form_res');
    }
}


function oxygen_admin_menu()
{
    $hide_partner_discounts = \Breakdance\Data\get_global_option('settings_hide_partner_discounts') === 'yes';

    if (\Breakdance\Permissions\hasPermission('full')) {
        /** @psalm-suppress UndefinedFunction  */
        add_menu_page(
            'Oxygen',
            'Oxygen',
            'manage_options',
            'oxygen',
            '',
            'data:image/svg+xml;base64,'
        );

        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('oxygen', 'Home', 'Home', 'edit_posts', 'oxygen', 'Breakdance\Admin\oxygenHomePage');
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('oxygen', 'Templates', 'Templates', 'edit_posts', BREAKDANCE_TEMPLATE_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('oxygen', 'Headers', 'Headers', 'edit_posts', BREAKDANCE_HEADER_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('oxygen', 'Footers', 'Footers', 'edit_posts', BREAKDANCE_FOOTER_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('oxygen', __bdox('global_blocks'), __bdox('global_blocks'), 'edit_posts', BREAKDANCE_BLOCK_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('oxygen', 'Design Library', 'Design Library', 'manage_options', 'breakdance_design_library', "Breakdance\DesignLibrary\getDesignLibraryAppLoader");
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('oxygen', __bdox('global_blocks'), __bdox('global_blocks'), 'manage_options', BREAKDANCE_BLOCK_POST_TYPE, "Breakdance\Themeless\ManageTemplates\getManageBreakdancePostTypesSpaHtml");
        /** @psalm-suppress UndefinedFunction  */
        add_submenu_page('oxygen', 'Design Library', 'Design Library', 'manage_options', 'oxygen_design_library', "Breakdance\DesignLibrary\getDesignLibraryAppLoader");
        if (!$hide_partner_discounts) {
            /** @psalm-suppress UndefinedFunction  */
            add_submenu_page('oxygen', 'Partner Discounts', 'Partner Discounts', 'manage_options', 'oxygen-partner-discounts', 'Breakdance\Admin\render_oxygen_partner_discounts_page');
        }

        bdox_run_action('breakdance_admin_menu');
    }
}
